INSERT INTO `pokemon_species_flavor_summaries` (`pokemon_species_id`, `local_language_id`, `flavor_summary`) VALUES
  (1, 10, 'Od narození má na zádech divné semínko, které roste a vyvíjí se spolecne s ním. Uvnitr má zásobu živin, kterou využívají zejména malí Bulbasauri. I pozdeji ale muže celé dny nejíst.\n\nBulbasauri rádi odpocívají na slunícku. Semínko na zádech jim tak roste rychleji.');
