drop DATABASE if exists HR;
-- -------------------------------------------------------------------------------------
create DATABASE HR default character set UTF8 collate UTF8_GENERAL_CI;
use HR;

/*==============================================================*/
/* Table: COUNTRIES                                             */
/*==============================================================*/
create table COUNTRIES
(
   COUNTRY_ID           char(2) 			not null,
   REGION_ID            numeric				null,
   COUNTRY_NAME         varchar(40)			null,
   primary key (COUNTRY_ID)
);

/*==============================================================*/
/* Table: DEPARTMENTS                                           */
/*==============================================================*/
create table DEPARTMENTS
(
   DEPARTMENT_ID        numeric(4)           not null,
   LOCATION_ID          numeric(4)           null,
   DEPARTMENT_NAME      varchar(30)          not null,
   MANAGER_ID           numeric(6)           null,
   primary key (DEPARTMENT_ID)
);

/*==============================================================*/
/* Table: EMPLOYEES                                             */
/*==============================================================*/
create table EMPLOYEES
(
   EMPLOYEE_ID          numeric(6)           not null,
   JOB_ID               varchar(10)          not null,
   DEPARTMENT_ID        numeric(4)           null,
   MANAGER_ID           numeric(6)           null,
   FIRST_NAME           varchar(20)          null,
   LAST_NAME            varchar(25)          not null,
   EMAIL                varchar(25)          not null,
   PHONE_NUMBER         varchar(20)          null,
   HIRE_DATE            date             not null,
   SALARY               numeric(8,2)         null,
   COMMISSION_PCT       numeric(2,2)         null,
   primary key (EMPLOYEE_ID)
);

/*==============================================================*/
/* Table: JOBS                                                  */
/*==============================================================*/
create table JOBS
(
   JOB_ID               varchar(10)          not null,
   JOB_TITLE            varchar(35)          not null,
   MIN_SALARY           numeric(6)           null,
   MAX_SALARY           numeric(6)           null,
   primary key (JOB_ID)
);

/*==============================================================*/
/* Table: JOB_HISTORY                                           */
/*==============================================================*/
create table JOB_HISTORY
(
   EMPLOYEE_ID          numeric(6)           not null,
   START_DATE           date             not null,
   END_DATE             date             not null,
   JOB_ID               varchar(10)          not null,
   DEPARTMENT_ID        numeric(4)           null,
   primary key (EMPLOYEE_ID, START_DATE)
);

/*==============================================================*/
/* Table: LOCATIONS                                             */
/*==============================================================*/
create table LOCATIONS
(
   LOCATION_ID          numeric(4)           not null,
   COUNTRY_ID           char(2)              null,
   STREET_ADDRESS       varchar(40)          null,
   POSTAL_CODE          varchar(12)          null,
   CITY                 varchar(30)          not null,
   STATE_PROVINCE       varchar(25)          null,
   primary key (LOCATION_ID)
);

/*==============================================================*/
/* Table: REGIONS                                               */
/*==============================================================*/
create table REGIONS
(
   REGION_ID            numeric              not null,
   REGION_NAME          varchar(25)          null,
   primary key (REGION_ID)
);








alter table COUNTRIES add constraint FK_COUNTRIES_REGIONS foreign key (REGION_ID)
      references REGIONS (REGION_ID) on delete restrict on update restrict;

alter table DEPARTMENTS add constraint FK_DEPARTMENTS_EMPLOYEES foreign key (MANAGER_ID)
      references EMPLOYEES (EMPLOYEE_ID) on delete restrict on update restrict;

alter table DEPARTMENTS add constraint FK_DEPARTMENTS_LOCATIONS foreign key (LOCATION_ID)
      references LOCATIONS (LOCATION_ID) on delete restrict on update restrict;

alter table EMPLOYEES add constraint FK_EMPLOYEES_DEPARTMENTS foreign key (DEPARTMENT_ID)
      references DEPARTMENTS (DEPARTMENT_ID) on delete restrict on update restrict;

alter table EMPLOYEES add constraint FK_EMPLOYEES_EMPLOYEES foreign key (MANAGER_ID)
      references EMPLOYEES (EMPLOYEE_ID) on delete restrict on update restrict;

alter table EMPLOYEES add constraint FK_EMPLOYEES_JOBS foreign key (JOB_ID)
      references JOBS (JOB_ID) on delete restrict on update restrict;

alter table JOB_HISTORY add constraint FK_JOB_HISTORY_DEPARTMENTS foreign key (DEPARTMENT_ID)
      references DEPARTMENTS (DEPARTMENT_ID) on delete restrict on update restrict;

alter table JOB_HISTORY add constraint FK_JOB_HISTORY_EMPLOYEES foreign key (EMPLOYEE_ID)
      references EMPLOYEES (EMPLOYEE_ID) on delete restrict on update restrict;

alter table JOB_HISTORY add constraint FK_JOB_HISTORY_JOBS foreign key (JOB_ID)
      references JOBS (JOB_ID) on delete restrict on update restrict;

alter table LOCATIONS add constraint FK_LOCATIONS_COUNTRIES foreign key (COUNTRY_ID)
      references COUNTRIES (COUNTRY_ID) on delete restrict on update restrict;